def egcd(a, b):
    if a == 0:
        return (b, 0, 1)
    else:
        gcd, x, y = egcd(b % a, a)
        return (gcd, y - (b // a) * x, x)

def modinv(a, n):
    gcd, x, y = egcd(a, n)
    if gcd != 1:
        return None  # a và n không cùng nguyên tố với nhau, không có nghịch đảo modulo
    else:
        return x % n
def int_to_ascii(m):
    # Decode to ascii (from https://crypto.stackexchange.com/a/80346)
    m_hex = hex(int(m))[2:-1]  # Number to hex
    m_ascii = "".join(
        chr(int(m_hex[i : i + 2], 16)) for i in range(0, len(m_hex), 2)
    )  # Hex to Ascii
    return m_ascii
def power(a, b, mod):
    if b == 0:
        return 1 % mod
    elif b % 2 == 0:
        p = power(a, b // 2, mod)
        return (p * p) % mod
    else:
        p = power(a, (b - 1) // 2, mod)
        return (a * p * p) % mod
